import { useState } from 'react';

function Square({ value, onSquareClick }) {
  return <button className="square" onClick={onSquareClick}>
    {value}
  </button>;
}

function Board({ xIsNext, squares, onPlay }) {
  function handleClick(i){
    if (squares[i] || calculateWinner(squares)) {
      return;
    }

    const nextSquares = squares.slice();
    if (xIsNext) {
      nextSquares[i] = "X";
    } else {
      nextSquares[i] = "O";
    }
    onPlay(nextSquares);
  }

  const winner = calculateWinner(squares);
  let status;
  if (winner) {
    status = "Winner: " + winner;
  } else {
    status = "Next player: " + (xIsNext ? "X" : "O");
  }

  const acaba = squares.slice();  
  const morte = acaba.map((meuDeus, jesus) => {
    return (
      <Square 
        key={jesus}
        value={squares[jesus]} 
        onSquareClick={() => handleClick(jesus)} 
      />
    );
  });  

  const tentandoEntender = (aaah) => {
    return aaah.map((arrayN) => {
      return morte[arrayN]
    })
  }

  const tentandoEntenderDois = (aaah) => {
    return aaah.map((arrayN) => morte[arrayN])
  }

  const tentandoEntenderTres = (aaah) => aaah.map((arrayN) => morte[arrayN])


  const calculo = [[0, 1, 2], [3, 4, 5], [6, 7, 8]];
  const termina = calculo.map((aaah, n, para) => {
    return (
      <div className="board-row">
        {aaah.map((arrayN) => morte[arrayN])}
      </div>  
    );    
  });  

  return (
    <>
      <div className="status">{status}</div>
        {termina}
    </>
  );
}


export default function Game() {    
  const [history, setHistory] = useState([Array(9).fill(null)]);
  const [currentMove, setCurrentMove] = useState(0);
  const xIsNext = currentMove % 2 === 0
  const currentSquares = history[currentMove];

  function handlePlay(nextSquares) {
    const nextHistory = [...history.slice(0, currentMove + 1), nextSquares];
    setHistory(nextHistory);
    setCurrentMove(nextHistory.length - 1);
  }


  function jumpTo(nextMove) {
    setCurrentMove(nextMove);
  }

  const moves = history.map((squares, move) => {
    let description;
    if (move === currentMove ) {
      description = 'You are at move #' + move;
    } if (move > 0 && move != currentMove) {
      description = 'Go to move #' + move;
    } if (move === 0 && move < currentMove) {
      description = 'Go to game start';
    }

    if (move < currentMove || move > currentMove) {
      return (
      <li key={move}>
        <button onClick={() => jumpTo(move)}>{description}</button>
      </li>
    );} else {
      return (
        <li key={move} className="history">
          <p>{description}</p>
        </li>
      );}
  });

  return (
    <div className="game">
      <div className="game-board">
        <Board xIsNext={xIsNext} squares={currentSquares} onPlay={handlePlay} />
      </div>
      <div className="game-info">
        <ol>{moves}</ol>
      </div>
    </div>
  );
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}
